import App from 'path/to/your/divination-app/App';

function YourApp() {
  return (
    <div>
      <header>你的应用头部</header>
      <App /> {/* 集成占卜应用 */}
    </div>
  );
}

// 使用single-spa等微前端框架
const lifecycles = {
  bootstrap: async () => {
    console.log('占卜应用开始加载');
  },
  mount: async () => {
    ReactDOM.render(
      <App />,
      document.getElementById('divination-app')
    );
  },
  unmount: async () => {
    ReactDOM.unmountComponentAtNode(
      document.getElementById('divination-app')
    );
  }
};
